@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=DM+Sans:wght@400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE - Refined Fintech Design System */
:root {
  --button-outline: rgba(0,0,0, .12);
  --badge-outline: rgba(0,0,0, .08);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  /* Refined Fintech Color Palette */
  --slate-950: 222 47% 11%; /* #0F172A - Deep navy for text */
  --slate-900: 222 47% 15%; /* #1E293B */
  --slate-800: 215 25% 27%; /* #334155 */
  --slate-700: 215 16% 47%; /* #64748B - Secondary text */
  --slate-100: 214 32% 91%; /* #E2E8F0 */
  --slate-50: 210 40% 98%; /* #F8FAFC - Subtle background */

  --indigo-600: 239 84% 67%; /* #6366F1 - Primary brand color */
  --indigo-700: 243 75% 59%; /* #4F46E5 - Hover state */
  --indigo-50: 226 100% 97%; /* #EEF2FF - Light accent */

  --emerald-600: 160 84% 39%; /* #10B981 - Success */
  --rose-600: 0 84% 60%; /* #F43F5E - Destructive */
  --amber-500: 38 92% 50%; /* #F59E0B - Warning */

  --neutral-50: 0 0% 98%; /* #FAFAFA */
  --neutral-100: 0 0% 96%; /* #F5F5F5 */
  --neutral-200: 0 0% 90%; /* #E5E5E5 */
  --neutral-300: 0 0% 83%; /* #D4D4D4 */
  --neutral-border: 220 13% 91%; /* Refined border */

  /* Semantic mappings */
  --background: var(--slate-50);
  --foreground: var(--slate-950);
  --border: var(--neutral-border);

  --card: 0 0% 100%;
  --card-foreground: var(--slate-950);
  --card-border: var(--neutral-200);

  --sidebar: var(--neutral-50);
  --sidebar-foreground: var(--slate-950);
  --sidebar-border: var(--neutral-200);
  --sidebar-primary: var(--slate-950);
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: var(--neutral-100);
  --sidebar-accent-foreground: var(--slate-950);
  --sidebar-ring: var(--indigo-600);

  --popover: 0 0% 100%;
  --popover-foreground: var(--slate-950);
  --popover-border: var(--neutral-200);

  --primary: var(--indigo-600);
  --primary-foreground: 0 0% 100%;

  --secondary: var(--neutral-100);
  --secondary-foreground: var(--slate-950);

  --muted: var(--neutral-100);
  --muted-foreground: var(--slate-700);

  --accent: var(--indigo-50);
  --accent-foreground: var(--indigo-700);

  --destructive: var(--rose-600);
  --destructive-foreground: 0 0% 100%;

  --input: var(--neutral-200);
  --ring: var(--indigo-600);

  --chart-1: 239 84% 67%;
  --chart-2: 160 84% 39%;
  --chart-3: 38 92% 50%;
  --chart-4: 0 84% 60%;
  --chart-5: 215 16% 47%;

  /* Typography */
  --font-sans: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --font-display: 'DM Sans', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --font-serif: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --font-mono: ui-monospace, SFMono-Regular, 'SF Mono', Menlo, Consolas, 'Liberation Mono', monospace;

  /* Spacing and Layout */
  --radius: 0.75rem; /* 12px - slightly tighter */
  --radius-card: 1rem; /* 16px - more refined than 24px */
  --spacing: 0.25rem;

  /* Shadows - refined and subtle */
  --shadow-card: 0px 1px 3px 0px rgba(0, 0, 0, 0.04), 0px 1px 2px 0px rgba(0, 0, 0, 0.03);
  --shadow-popover: 0px 4px 16px 0px rgba(0, 0, 0, 0.06), 0px 2px 4px 0px rgba(0, 0, 0, 0.04);
  --shadow-2xs: 0px 1px 2px 0px rgba(0, 0, 0, 0.03);
  --shadow-xs: 0px 1px 3px 0px rgba(0, 0, 0, 0.06);
  --shadow-sm: 0px 2px 4px -1px rgba(0, 0, 0, 0.04), 0px 1px 2px -1px rgba(0, 0, 0, 0.03);
  --shadow: 0px 4px 6px -1px rgba(0, 0, 0, 0.06), 0px 2px 4px -1px rgba(0, 0, 0, 0.04);
  --shadow-md: 0px 6px 12px -2px rgba(0, 0, 0, 0.08), 0px 3px 6px -2px rgba(0, 0, 0, 0.06);
  --shadow-lg: 0px 10px 20px -4px rgba(0, 0, 0, 0.10), 0px 4px 8px -2px rgba(0, 0, 0, 0.06);
  --shadow-xl: 0px 20px 30px -6px rgba(0, 0, 0, 0.12), 0px 8px 12px -4px rgba(0, 0, 0, 0.08);
  --shadow-2xl: 0px 25px 50px -12px rgba(0, 0, 0, 0.15);

  --tracking-normal: -0.02em;

  /* Automatically computed borders */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

/* DARK MODE */
.dark {
  --button-outline: rgba(255,255,255, .12);
  --badge-outline: rgba(255,255,255, .08);

  --opaque-button-border-intensity: 9;

  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .10);

  /* Dark mode colors - refined */
  --background: var(--slate-950);
  --foreground: var(--slate-50);
  --border: 215 25% 20%;

  --card: var(--slate-900);
  --card-foreground: var(--slate-50);
  --card-border: 215 25% 20%;

  --sidebar: 222 47% 8%;
  --sidebar-foreground: var(--slate-50);
  --sidebar-border: 215 25% 20%;
  --sidebar-primary: var(--slate-50);
  --sidebar-primary-foreground: var(--slate-950);
  --sidebar-accent: 215 25% 18%;
  --sidebar-accent-foreground: var(--slate-50);
  --sidebar-ring: var(--indigo-600);

  --popover: var(--slate-900);
  --popover-foreground: var(--slate-50);
  --popover-border: 215 25% 20%;

  --primary: var(--indigo-600);
  --primary-foreground: 0 0% 100%;

  --secondary: 215 25% 20%;
  --secondary-foreground: var(--slate-50);

  --muted: 215 25% 18%;
  --muted-foreground: var(--slate-700);

  --accent: var(--indigo-50);
  --accent-foreground: var(--indigo-700);

  --destructive: var(--rose-600);
  --destructive-foreground: 0 0% 100%;

  --input: 215 25% 25%;
  --ring: var(--indigo-600);

  --chart-1: 239 84% 67%;
  --chart-2: 160 84% 39%;
  --chart-3: 38 92% 50%;
  --chart-4: 0 84% 60%;
  --chart-5: 215 16% 47%;

  --shadow-card: 0px 2px 8px 0px rgba(0, 0, 0, 0.30), 0px 1px 2px 0px rgba(0, 0, 0, 0.20);
  --shadow-popover: 0px 4px 16px 0px rgba(0, 0, 0, 0.40), 0px 2px 4px 0px rgba(0, 0, 0, 0.30);
  --shadow-2xs: 0px 1px 2px 0px rgba(0, 0, 0, 0.30);
  --shadow-xs: 0px 1px 3px 0px rgba(0, 0, 0, 0.35);
  --shadow-sm: 0px 2px 4px -1px rgba(0, 0, 0, 0.30), 0px 1px 2px -1px rgba(0, 0, 0, 0.25);
  --shadow: 0px 4px 6px -1px rgba(0, 0, 0, 0.35), 0px 2px 4px -1px rgba(0, 0, 0, 0.30);
  --shadow-md: 0px 6px 12px -2px rgba(0, 0, 0, 0.40), 0px 3px 6px -2px rgba(0, 0, 0, 0.35);
  --shadow-lg: 0px 10px 20px -4px rgba(0, 0, 0, 0.45), 0px 4px 8px -2px rgba(0, 0, 0, 0.35);
  --shadow-xl: 0px 20px 30px -6px rgba(0, 0, 0, 0.50), 0px 8px 12px -4px rgba(0, 0, 0, 0.40);
  --shadow-2xl: 0px 25px 50px -12px rgba(0, 0, 0, 0.60);

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Typography hierarchy - Refined fintech style */
  h1 {
    @apply text-[2rem] leading-[2.25rem] font-bold tracking-tight;
  }

  h2 {
    @apply text-2xl leading-7 font-bold tracking-tight;
  }

  h3 {
    @apply text-xl leading-6 font-semibold tracking-tight;
  }

  /* Enable tabular nums for better number display */
  .tabular-nums {
    font-variant-numeric: tabular-nums;
  }
}

@layer utilities {
  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Custom shadow utilities */
  .shadow-card {
    box-shadow: var(--shadow-card);
  }

  .shadow-popover {
    box-shadow: var(--shadow-popover);
  }
}

